import React from "react";

export const Hero = () => (
  <section className="min-h-screen flex flex-col justify-center items-center bg-gray-900 text-white p-6">
    <h1 className="text-4xl md:text-6xl font-bold mb-4">KLOUGBA Gildas</h1>
    <h2 className="text-2xl md:text-3xl mb-6">Développeur Web & Mobile | UI/UX Designer</h2>
    <p className="text-center max-w-xl">
      Je crée des sites web professionnels et des applications mobiles dynamiques, avec un design moderne et responsive.
    </p>
  </section>
);
