// Hero.jsx - Design Moderne
import React, { useState, useEffect } from "react";
import "../css/Hero.css";

export const Hero = () => {
  const [currentText, setCurrentText] = useState(0);
  const [isVisible, setIsVisible] = useState(false);
  const [isTyping, setIsTyping] = useState(false);

  const specialties = [
    "D√©veloppeur Full-Stack",
    "UI/UX Designer", 
    "Expert React & Node.js",
    "Cr√©ateur d'Apps Mobiles"
  ];

  useEffect(() => {
    setIsVisible(true);
    const interval = setInterval(() => {
      setIsTyping(true);
      setTimeout(() => {
        setCurrentText((prev) => (prev + 1) % specialties.length);
        setIsTyping(false);
      }, 300);
    }, 4000);
    return () => clearInterval(interval);
  }, []);

  const handleScrollToProjects = () => {
    const projectsSection = document.getElementById('projects');
    if (projectsSection) {
      projectsSection.scrollIntoView({ behavior: 'smooth' });
    }
  };

  const handleContact = () => {
    const contactSection = document.getElementById('contact');
    if (contactSection) {
      contactSection.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <section className="hero-section">
      {/* Arri√®re-plan anim√© moderne */}
      <div className="hero-bg">
        <div className="floating-shape shape-1"></div>
        <div className="floating-shape shape-2"></div>
        <div className="floating-shape shape-3"></div>
        <div className="grid-overlay"></div>
      </div>

      <div className={`hero-content ${isVisible ? 'visible' : ''}`}>

        {/* Avatar Profile moderne */}
        <div className="profile-avatar">
          <div className="avatar-circle">
            <span className="avatar-initials">KG</span>
          </div>
          <div className="avatar-glow"></div>
        </div>

        {/* Titre principal */}
        <h1 className="hero-title">KLOUGBA Gildas</h1>

        {/* Sous-titre anim√© avec effet de frappe */}
        <div className="subtitle-container">
          <h2 className="hero-subtitle">
            <span className={`rotating-text ${isTyping ? 'typing' : ''}`}>
              {specialties[currentText]}
            </span>
            <span className="cursor">|</span>
          </h2>
        </div>

        {/* Description moderne */}
        <p className="hero-description">
          Passionn√© par les technologies web modernes, je transforme vos id√©es en 
          <span className="highlight"> exp√©riences num√©riques exceptionnelles</span>. 
          De la conception √† la mise en production, je vous accompagne dans tous vos projets digitaux.
        </p>

        {/* Tags de comp√©tences avec animations */}
        <div className="skills-container">
          {[
            { name: "React"},
            { name: "Node.js", icon: "üü¢" },
            { name: "TypeScript", icon: "üî∑" },
            { name: "MongoDB", icon: "üçÉ" },
            { name: "React Native", icon: "üì±" },
            { name: "Figma", icon: "üé®" },
            { name: "AWS", icon: "‚òÅÔ∏è" },
            { name: "Docker", icon: "üê≥" }
          ].map((skill, index) => (
            <span 
              key={skill.name} 
              className="skill-tag"
              style={{ animationDelay: `${index * 150}ms` }}
            >
              <span className="skill-icon">{skill.icon}</span>
              {skill.name}
            </span>
          ))}
        </div>

        {/* Boutons d'action modernes */}
        <div className="cta-buttons">
          <button className="btn-primary" onClick={handleScrollToProjects}>
            <span>Voir mes projets</span>
            <div className="btn-overlay"></div>
            <svg className="btn-icon" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <path d="M7 17L17 7M17 7H7M17 7V17"/>
            </svg>
          </button>
          <button className="btn-secondary" onClick={handleContact}>
            <span>Me contacter</span>
            <svg className="btn-icon" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <path d="M21 15a2 2 0 0 1-2 2H7l-4 4V5a2 2 0 0 1 2-2h14a2 2 0 0 1 2 2z"/>
            </svg>
          </button>
        </div>

        {/* Indicateur de scroll moderne */}
        <div className="scroll-indicator">
          <span className="scroll-text">D√©couvrez mon travail</span>
          <div className="scroll-mouse">
            <div className="scroll-wheel"></div>
          </div>
        </div>

        {/* Liens sociaux modernes */}
        <div className="social-links">
          <a href="https://linkedin.com/in/your-profile" target="_blank" rel="noopener noreferrer" className="social-link" aria-label="LinkedIn">
            <svg width="24" height="24" viewBox="0 0 24 24" fill="currentColor">
              <path d="M20.447 20.452h-3.554v-5.569c0-1.328-.027-3.037-1.852-3.037-1.853 0-2.136 1.445-2.136 2.939v5.667H9.351V9h3.414v1.561h.046c.477-.9 1.637-1.85 3.37-1.85 3.601 0 4.267 2.37 4.267 5.455v6.286zM5.337 7.433c-1.144 0-2.063-.926-2.063-2.065 0-1.138.92-2.063 2.063-2.063 1.14 0 2.064.925 2.064 2.063 0 1.139-.925 2.065-2.064 2.065zm1.782 13.019H3.555V9h3.564v11.452zM22.225 0H1.771C.792 0 0 .774 0 1.729v20.542C0 23.227.792 24 1.771 24h20.451C23.2 24 24 23.227 24 22.271V1.729C24 .774 23.2 0 22.222 0h.003z"/>
            </svg>
          </a>
          <a href="https://github.com/your-username" target="_blank" rel="noopener noreferrer" className="social-link" aria-label="GitHub">
            <svg width="24" height="24" viewBox="0 0 24 24" fill="currentColor">
              <path d="M12 0c-6.626 0-12 5.373-12 12 0 5.302 3.438 9.8 8.207 11.387.599.111.793-.261.793-.577v-2.234c-3.338.726-4.033-1.416-4.033-1.416-.546-1.387-1.333-1.756-1.333-1.756-1.089-.745.083-.729.083-.729 1.205.084 1.839 1.237 1.839 1.237 1.07 1.834 2.807 1.304 3.492.997.107-.775.418-1.305.762-1.604-2.665-.305-5.467-1.334-5.467-5.931 0-1.311.469-2.381 1.236-3.221-.124-.303-.535-1.524.117-3.176 0 0 1.008-.322 3.301 1.23.957-.266 1.983-.399 3.003-.404 1.02.005 2.047.138 3.006.404 2.291-1.552 3.297-1.23 3.297-1.23.653 1.653.242 2.874.118 3.176.77.84 1.235 1.911 1.235 3.221 0 4.609-2.807 5.624-5.479 5.921.43.372.823 1.102.823 2.222v3.293c0 .319.192.694.801.576 4.765-1.589 8.199-6.086 8.199-11.386 0-6.627-5.373-12-12-12z"/>
            </svg>
          </a>
          <a href="mailto:contact@example.com" className="social-link" aria-label="Email">
            <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <path d="M4 4h16c1.1 0 2 .9 2 2v12c0 1.1-.9 2-2 2H4c-1.1 0-2-.9-2-2V6c0-1.1.9-2 2-2z"/>
              <polyline points="22,6 12,13 2,6"/>
            </svg>
          </a>
        </div>
      </div>
    </section>
  );
};
